INSERT INTO `email_templates_default_data` VALUES
('NOTIFICATION','en_US','New notification from {$siteTitle}','You have a new notification from {$siteTitle}:\n\n{$notificationContents}\n\nLink: {$url}\n\n{$principalContactSignature}','The email is sent to registered users that have selected to have this type of notification emailed to them.'),
('NOTIFICATION_MAILLIST','en_US','New notification from {$siteTitle}','You have a new notification from {$siteTitle}:\n--\n{$notificationContents}\n\nLink: {$url}\n--\n\nIf you wish to stop receiving notification emails, please go to {$unsubscribeLink} and enter your email address and password.\n\n{$principalContactSignature}','This email is sent to unregistered users on the notification mailing list.'),
('NOTIFICATION_MAILLIST_WELCOME','en_US','Welcome to the the {$siteTitle} mailing list!','You have signed up to receive notifications from {$siteTitle}.\n			\nPlease click on this link to confirm your request and add your email address to the mailing list: {$confirmLink}\n\nIf you wish to stop receiving notification emails, please go to {$unsubscribeLink} and enter your email address and password.\n\nYour password for disabling notification emails is: {$password}\n\n{$principalContactSignature}','This email is sent to an unregistered user who just registered with the notification mailing list.'),
('NOTIFICATION_MAILLIST_PASSWORD','en_US','Your notification mailing list information for {$siteTitle}','Your new password for disabling notification emails is: {$password}\n\nIf you wish to stop receiving notification emails, please go to {$unsubscribeLink} and enter your email address and password.\n\n{$principalContactSignature}','This email is sent to an unregistered user on the notification mailing list when they indicate that they have forgotten their password or are unable to login. It provides a URL they can follow to reset their password.'),
('PASSWORD_RESET_CONFIRM','en_US','Password Reset Confirmation','We have received a request to reset your password for the {$siteTitle} web site.\n\nIf you did not make this request, please ignore this email and your password will not be changed. If you wish to reset your password, click on the below URL.\n\nReset my password: {$url}\n\n{$principalContactSignature}','This email is sent to a registered user when they indicate that they have forgotten their password or are unable to login. It provides a URL they can follow to reset their password.'),
('PASSWORD_RESET','en_US','Password Reset','Your password has been successfully reset for use with the {$siteTitle} web site. Please retain this username and password, as it is necessary for all work with the conference.\n\nYour username: {$username}\nYour new password: {$password}\n\n{$principalContactSignature}','This email is sent to a registered user when they have successfully reset their password following the process described in the PASSWORD_RESET_CONFIRM email.'),
('USER_REGISTER','en_US','New User Registration','Thank you for registering as a user with {$conferenceName}. Please keep track of your username and password, which are needed for all work with this conference.\n\nUsername: {$username}\nPassword: {$password}\n\nThank you,\n{$principalContactSignature}','This email is sent to a newly registered user to welcome them to the system and provide them with a record of their username and password.'),
('USER_VALIDATE','en_US','Validate Your Account','{$userFullName}\n\nYou have created an account with {$conferenceName}, but before you can start using it, you need to validate your email account. To do this, simply follow the link below:\n\n{$activateUrl}\n\nThank you,\n{$principalContactSignature}','This email is sent to a newly created user to welcome them to the system and provide them with a record of their username and password.'),
('SUBMISSION_ACK','en_US','Submission Acknowledgement','{$authorName}:\n\nThank you for your submission, \"{$paperTitle}\" to {$conferenceName}. With the online conference management system that we are using, you will be able to track its progress through the editorial process by logging in to the conference web site:\n\nSubmission URL: {$submissionUrl}\nUsername: {$authorUsername}\n\nIf you have any questions, please contact me. Thank you for considering this conference as a venue for your work.\n\n{$editorialContactSignature}','This email, when enabled, is automatically sent to a author when he or she completes the process of submitting a paper or abstract to the conference. It provides information about tracking the submission through the process and thanks the author for the submission.'),
('SUBMISSION_UPLOAD_ACK','en_US','Submission Upload Acknowledgement','{$authorName}:\n\nThank you for uploading your presentation, \"{$paperTitle}\" to {$conferenceName}. With the online conference management system that we are using, you will be able to track its progress through the editorial process by logging in to the conference web site:\n\nSubmission URL: {$submissionUrl}\nUsername: {$authorUsername}\n\nIf you have any questions, please contact me. Thank you for considering this conference as a venue for your work.\n\n{$editorialContactSignature}','This email, when enabled, is automatically sent to a author when he or she completes the process of submitting a paper or abstract to the conference. It provides information about tracking the submission through the process and thanks the author for the submission.'),
('SUBMISSION_UNSUITABLE','en_US','Unsuitable Submission','{$authorName}:\n\nAn initial review of \"{$paperTitle}\" has made it clear that this submission does not fit within the scope and focus of {$conferenceName}. I recommend that you consult the description of this conference under About, as well as any previous scheduled conferences, to learn more about the work that we accept. You might also consider submitting this paper or abstract to another, more suitable conference.\n\n{$editorialContactSignature}',''),
('SUBMISSION_COMMENT','en_US','Submission Comment','{$name}:\n\n{$commentName} has added a comment to the submission, \"{$paperTitle}\" in {$conferenceName}:\n\n{$comments}','This email notifies the various people involved in a submission\'s editing process that a new comment has been posted.'),
('SUBMISSION_DECISION_REVIEWERS','en_US','Decision on \"{$paperTitle}\"','As one of the reviewers for the submission, \"{$paperTitle},\" to {$conferenceName}, I am sending you the reviews and editorial decision sent to the author of this piece. Thank you again for your important contribution to this process.\n \n{$editorialContactSignature}\n\n{$comments}','This email notifies the reviewers of a submission that the review process has been completed. It includes information about the paper and the decision reached, and thanks the reviewers for their contributions.'),
('DIRECTOR_ASSIGN','en_US','Director Assignment','{$editorialContactName}:\n\nThe submission, \"{$paperTitle},\" to {$conferenceName} has been assigned to you to see through the editorial process in your role as Track Director.  \n\nSubmission URL: {$submissionUrl}\nUsername: {$directorUsername}\n\nThank you,\n{$editorialContactSignature}','This email notifies a Track Director that the Director has assigned them the task of overseeing a submission through the editing process. It provides information about the submission and how to access the conference site.'),
('REVIEW_REQUEST','en_US','Paper Review Request','{$reviewerName}:\n\nI believe that you would serve as an excellent reviewer of the proposal, \"{$paperTitle},\" which has been submitted to {$conferenceName}. The submission\'s extract is inserted below, and I hope that you will consider undertaking this important task for us.\n\nPlease log into the conference web site by {$weekLaterDate} to indicate whether you will undertake the review or not, as well as to access the submission and to record your review and recommendation. The web site is {$conferenceUrl}\n\nThe review itself is due {$reviewDueDate}.\n\nIf you do not have your username and password for the conference\'s web site, you can use this link to reset your password (which will then be emailed to you along with your username). {$passwordResetUrl}\n\nSubmission URL: {$submissionReviewUrl}\n\nThank you for considering this request.\n\n{$editorialContactSignature}\n\n\n\n\"{$paperTitle}\"\n\nAbstract\n{$paperAbstract}','This email from the Track Director to a Reviewer requests that the reviewer accept or decline the task of reviewing a submission. It provides information about the submission such as the title and abstract, a review due date, and how to access the submission itself.'),
('REVIEW_REQUEST_ONECLICK','en_US','Paper Review Request','{$reviewerName}:\n\nI believe that you would serve as an excellent reviewer of the submission, \"{$paperTitle},\" which has been submitted to {$conferenceName}. The submission\'s extract is inserted below, and I hope that you will consider undertaking this important task for us.\n\nPlease log into the conference web site by {$weekLaterDate} to indicate whether you will undertake the review or not, as well as to access the submission and to record your review and recommendation.\n\nThe review itself is due {$reviewDueDate}.\n\nSubmission URL: {$submissionReviewUrl}\n\nThank you for considering this request.\n\n{$editorialContactSignature}\n\n\n\n\"{$paperTitle}\"\n\nAbstract\n{$paperAbstract}','This email from the Track Director to a Reviewer requests that the reviewer accept or decline the task of reviewing a submission. It provides information about the submission such as the title and abstract, a review due date, and how to access the submission itself. This message is used when the Standard Review Process is selected in Conference Setup, Step 2, and one-click reviewer access is enabled.'),
('REVIEW_CANCEL','en_US','Request for Review Cancelled','{$reviewerName}:\n\nWe have decided at this point to cancel our request for you to review the submission, \"{$paperTitle},\" for {$conferenceName}. We apologize for any inconvenience this may cause you and hope that we will be able to call on you to assist with this conference\'s review process in the future.\n\nIf you have any questions, please contact me.\n\n{$editorialContactSignature}','This email is sent by the Track Director to a Reviewer who has a submission review in progress to notify them that the review has been cancelled.'),
('REVIEW_CONFIRM','en_US','Able to Review','{$editorialContactName}:\n\nI am able and willing to review the submission, \"{$paperTitle},\" for {$conferenceName}. Thank you for thinking of me, and I plan to have the review completed by its due date, {$reviewDueDate}, if not before.\n\n{$reviewerName}','This email is sent by a Reviewer to the Track Director in response to a review request to notify the Track Director that the review request has been accepted and will be completed by the specified date.'),
('REVIEW_CONFIRM_ACK','en_US','Review Underway Acknowledgement','{$reviewerName}:\n\nThank you for agreeing to review the submission \"{$paperTitle}\" for {$conferenceName} by {$reviewDueDate}.\n\n{$editorialContactSignature}','This email is sent from the Track Director to the Reviewer to acknowledge their acceptance of a review or encourage them to complete the review.'),
('REVIEW_DECLINE','en_US','Unable to Review','{$editorialContactName}:\n\nI am afraid that at this time I am unable to review the submission, \"{$paperTitle},\" for {$conferenceName}. Thank you for thinking of me, and another time feel free to call on me.\n\n{$reviewerName}','This email is sent by a Reviewer to the Track Director in response to a review request to notify the Track Director that the review request has been declined.'),
('REVIEW_COMPLETE','en_US','Paper Review Completed','{$editorialContactName}:\n\nI have now completed my review of \"{$paperTitle}\" for {$conferenceName}, and submitted my recommendation, \"{$recommendation}.\"\n\n{$reviewerName}','This email is sent by a Reviewer to the Track Director to notify them that a review has been completed and the comments and recommendations have been recorded on the conference web site.'),
('REVIEW_ACK','en_US','Paper Review Acknowledgement','{$reviewerName}:\n\nThank you for completing the review of the submission, \"{$paperTitle},\" for {$conferenceName}. We appreciate your contribution to the quality of the work that we produce.\n\n{$editorialContactSignature}','This email is sent by a Track Director to confirm receipt of a completed review and thank the reviewer for their contributions.'),
('REVIEW_REMIND','en_US','Submission Review Reminder','{$reviewerName}:\n\nJust a gentle reminder of our request for your review of the submission, \"{$paperTitle},\" for {$conferenceName}. We were hoping to have this review by {$reviewDueDate}, and would be pleased to receive it as soon as you are able to prepare it.\n\nIf you do not have your username and password for the conference\'s web site, you can use this link to reset your password (which will then be emailed to you along with your username). {$passwordResetUrl}\n\nSubmission URL: {$submissionReviewUrl}\n\nPlease confirm your ability to complete this vital contribution to the work of the conference. I look forward to hearing from you.\n\n{$editorialContactSignature}','This email is sent by a Track Director to remind a reviewer that their review is due.'),
('REVIEW_REMIND_ONECLICK','en_US','Submission Review Reminder','{$reviewerName}:\n\nJust a gentle reminder of our request for your review of the submission, \"{$paperTitle},\" for {$conferenceName}. We were hoping to have this review by {$reviewDueDate}, and would be pleased to receive it as soon as you are able to prepare it.\n\nSubmission URL: {$submissionReviewUrl}\n\nPlease confirm your ability to complete this vital contribution to the work of the conference. I look forward to hearing from you.\n\n{$editorialContactSignature}','This email is sent by a Track Director to remind a reviewer that their review is due.'),
('REVIEW_REMIND_AUTO','en_US','Automated Submission Review Reminder','{$reviewerName}:\n\nJust a gentle reminder of our request for your review of the submission, \"{$paperTitle},\" for {$conferenceName}. We were hoping to have this review by {$reviewDueDate}, and this email has been automatically generated and sent with the passing of that date. We would still be pleased to receive it as soon as you are able to prepare it.\n\nIf you do not have your username and password for the conference\'s web site, you can use this link to reset your password (which will then be emailed to you along with your username). {$passwordResetUrl}\n\nSubmission URL: {$submissionReviewUrl}\n\nPlease confirm your ability to complete this vital contribution to the work of the conference. I look forward to hearing from you.\n\n{$editorialContactSignature}','This email is automatically sent when a reviewer\'s due date elapses (see Review Options under Conference Setup, Step 2) and one-click reviewer access is disabled. Scheduled tasks must be enabled and configured (see the site configuration file).'),
('REVIEW_REMIND_AUTO_ONECLICK','en_US','Automated Submission Review Reminder','{$reviewerName}:\n\nJust a gentle reminder of our request for your review of the submission, \"{$paperTitle},\" for {$conferenceName}. We were hoping to have this review by {$reviewDueDate}, and this email has been automatically generated and sent with the passing of that date. We would still be pleased to receive it as soon as you are able to prepare it.\n\nSubmission URL: {$submissionReviewUrl}\n\nPlease confirm your ability to complete this vital contribution to the work of the conference. I look forward to hearing from you.\n\n{$editorialContactSignature}','This email is automatically sent when a reviewer\'s due date elapses (see Review Options under Conference Setup, Step 2) and one-click reviewer access is enabled. Scheduled tasks must be enabled and configured (see the site configuration file).'),
('SUBMISSION_ABSTRACT_ACCEPT','en_US','Editorial Decision on Abstract','{$authorName}:\n\nCongratulations, your abstract {$paperTitle} has been accepted for presentation at {$conferenceTitle} which is being held {$conferenceDate} at {$locationCity}. \n\nThank you and looking forward to your participation in this event.\n{$editorialContactSignature}','This email is sent by a Track Director to a submission\'s Author to notify them that a decision has been reached in regard to a submission.'),
('SUBMISSION_ABSTRACT_DECLINE','en_US','Editorial Decision on Paper','{$authorName}:\n\nWe have now completed the review of your submission \"{$paperTitle}.\" As a result...\n\n{$editorialContactSignature}','This email is sent by a Track Director to a submission\'s Author to notify them that a decision has been reached in regard to a submission.'),
('SUBMISSION_ABSTRACT_REVISE','en_US','Editorial Decision on Paper','{$authorName}:\n\nAfter a careful review of your submission, \"{$paperTitle}\" will be considered for presentation at {$conferenceTitle} if the following revisions are successfully implemented.\n\nThank you and looking forward to your participation in this event.\n\n{$editorialContactSignature}','This email is sent by a Track Director to a submission\'s Author to notify them that a decision has been reached in regard to a submission.'),
('SUBMISSION_PAPER_ACCEPT','en_US','Editorial Decision on Paper','{$authorName}:\n\nCongratulations, your submission {$paperTitle} has been accepted for presentation at {$conferenceTitle} which is being held {$conferenceDate} at {$locationCity}. \n\nThank you and looking forward to your participation in this event.\n{$editorialContactSignature}','This email is sent by a Track Director to a submission\'s Author to notify them that a decision has been reached in regard to a submission.'),
('SUBMISSION_PAPER_DECLINE','en_US','Editorial Decision on Paper','{$authorName}:\n\nWe have now completed the review of your submission \"{$paperTitle}.\" As a result...\n\n{$editorialContactSignature}','This email is sent by a Track Director to a submission\'s Author to notify them that a decision has been reached in regard to a submission.'),
('SUBMISSION_PAPER_REVISE','en_US','Editorial Decision on Paper','{$authorName}:\n\nAfter a careful review of your submission, \"{$paperTitle}\" will be considered for presentation at {$conferenceTitle} if the following revisions are successfully implemented.\n\nThank you and looking forward to your participation in this event.\n\n{$editorialContactSignature}','This email is sent by a Track Director to a submission\'s Author to notify them that a decision has been reached in regard to a submission.'),
('SUBMISSION_PAPER_INVITE','en_US','Editorial Decision on Abstract','{$authorName}:\n\nCongratulations, your abstract {$paperTitle} has been accepted for presentation at {$conferenceTitle} which is being held {$conferenceDate} at {$locationCity}. You may now submit your paper for further review.\n\nThank you and looking forward to your participation in this event.\n{$editorialContactSignature}','This email is sent by a Track Director to a submission\'s Author to notify them that a decision has been reached in regard to an abstract and invite them to submit a paper for subsequent review.'),
('REGISTRATION_NOTIFY','en_US','Registration Notification','{$registrantName}:\n\nYou have now been registered as a registrant in our online conference management system for {$conferenceName}, with the following registration:\n\n{$registrationType}\n\nTo access content that is available only to registrants, simply log in to the system with your username, \"{$username}\".\n\nOnce you have logged in to the system you can change your profile details and password at any point.\n\nPlease note that if you have an institutional registration, there is no need for users at your institution to log in, since requests for registration content will be automatically authenticated by the system.\n\nIf you have any questions, please feel free to contact me.\n\n{$registrationContactSignature}','This email notifies a registered reader that the Manager has created a registration for them. It provides the conference\'s URL along with instructions for access.'),
('OPEN_ACCESS_NOTIFY','en_US','Issue Now Open Access','Readers:\n\n{$conferenceName} has just made available in an open access format the following issue. We invite you to review the Table of Contents here and then visit our web site ({$conferenceUrl}) to review papers and items of interest.\n\nThanks for the continuing interest in our work,\n{$editorialContactSignature}','This email is sent to registered readers who have requested to receive a notification email when an issue becomes open access.'),
('MANUAL_PAYMENT_RECEIVED','en_US','Your payment for {$conferenceName} has been received','{$registrantName}:\n\nYour payment for {$conferenceName} has been received and recorded in your account.\n\nIf you have any questions, please feel free to contact me.\n\n{$registrationContactSignature}','This email notifies a registered reader that the Manager has created a registration for them. It provides the conference\'s URL along with instructions for access.'),
('PASSWORD_RESET_CONFIRM','pt_PT','Confirmação de Recuperação de Senha','Recebemos um pedido de recuperação da sua senha para o portal {$siteTitle}.\n\nSe você não realizou este pedido, por favor ignore este e-mail e a sua senha não será alterada. Se deseja recuperar a sua senha, clique no endereço abaixo.\n\nRecuperar a minha senha: {$url}\n\n{$principalContactSignature}','Este e-mail é enviado aos utilizadores inscritos quando esqueceram a sua senha e não conseguem efectuar o login. É enviado um endereço para que eles possam recuperar a sua senha.'),
('PASSWORD_RESET','pt_PT','Senha Recuperada','A sua senha foi recuperada com sucesso para o portal {$siteTitle}. Por favor, guarde o nome de utilizador e senha, estes dados são necessários em futuros contactos com o sistema da conferência.\n\nO seu nome de utilizador: {$username}\nA sua senha: {$password}\n\n{$principalContactSignature}','Este e-mail é enviado aos utilizadores inscritos quando eles recuperam a sua senha, ao efectuarem, com sucesso, o processo descrito no e-mail PASSWORD_RESET_CONFIRM.'),
('USER_REGISTER','pt_PT','Inscrição de Novo Utilizador','Obrigado pela sua inscrição no evento: {$conferenceName}. Por favor, anote os seus dados abaixo discriminados, pois serão necessários em futuros contactos.\n\nUtilizador: {$username}\nSenha: {$password}\n\nObrigado,\n{$principalContactSignature}','Este e-mail é enviado aos utilizadores recém inscritos apresentando as boas-vindas ao sistema e informando-os sobre os seus dados de utilizador e senha.'),
('SUBMISSION_ACK','pt_PT','Aviso de recebimento de Submissão','{$presenterName}:\n\nObrigado pela sua sua submissão, \"{$paperTitle}\" ao evento: {$conferenceName}. Com o nosso sistema de gestão de conferências on-line, você será capaz de acompanhar o progresso da sua submissão, através do processo editorial, acedendo ao portal da conferência:\n\nURL da submissão: {$submissionUrl}\nNome de utilizador: {$presenterUsername}\n\nQuaisquer dúvidas, por favor entre em contacto. Obrigado por considerar esta conferência um local para o seu trabalho.\n\n{$editorialContactSignature}','Este e-mail, quando activo, é enviado automaticamente para o autor quando ele(a) finaliza o processo de submissão de um artigo ou resumo \"Abstract\" à conferência. Fornece informações sobre o acompanhamento do processo de submissão e agradece ao autor a submissão feita.'),
('SUBMISSION_UPLOAD_ACK','pt_PT','Aviso de recebimento de arquivo de submissão','{$presenterName}:\n\nObrigado por enviar a sua apresentação, \"{$paperTitle}\" ao evento: {$conferenceName}. Com o nosso sistema de gestão de conferências on-line, você será capaz de acompanhar o progresso da sua submissão, através do processo editorial ao aceder ao portal da conferência:\n\nURL da submissão: {$submissionUrl}\nNome de utilizador: {$presenterUsername}\n\nQuaisquer dúvidas, por favor entre em contacto. Obrigado por considerar esta conferência um local para o seu trabalho.\n\n{$editorialContactSignature}','Este e-mail, quando activado, é enviado automaticamente para o autor quando ele(a) finaliza o processo de submeter um artigo ou resumo \"Abstract\" à conferência. Fornece informações sobre o acompanhamento do processo de submissão e agradece ao autor pela submissão.'),
('SUBMISSION_UNSUITABLE','pt_PT','Submissão não apropriada','{$presenterName}:\n\nApós uma revisão inicial de \"{$paperTitle}\", ficou claro que a submissão não é direccionada ao âmbito e foco do evento: {$conferenceName}. Recomendo que consulte a descrição desta conferência no menu \"Sobre\", assim como outras conferência anteriores, para saber mais sobre os trabalhos que aceitamos. Considere apresentar a submissão deste artigo ou resumo \"abstract\" a outra conferência mais apropriada.\n\n\n{$editorialContactSignature}',''),
('SUBMISSION_COMMENT','pt_PT','Comentário da Submissão','{$name}:\n\n{$commentName} adicionou um comentário à sua submissão, \"{$paperTitle}\" no evento: {$conferenceName}:\n\n{$comments}','Este e-mail notifica várias pessoas envolvidas no processo de editoração de uma submissão que recebeu um novo comentário'),
('SUBMISSION_DECISION_REVIEWERS','pt_PT','Decisão sobre \"{$paperTitle}\"','Como um dos avaliadores da submissão, \"{$paperTitle},\" ao evento: {$conferenceName}, envio-lhe as revisões e decisões editorias enviadas ao palestrante desta parte.\nObrigado novamente pela sua importante participação neste processo.\n \n{$editorialContactSignature}\n\n{$comments}','Este e-mail informa os avaliadores de uma submissão que o processo de revisão foi finalizado. Inclui informações sobre o artigo e a decisão tomada e agradece aos avaliadores pelas suas contribuições'),
('DIRECTOR_ASSIGN','pt_PT','Tarefa designada pelo Presidente da Comissão Organizadora','{$editorialContactName}:\n\nA submissão, \"{$paperTitle},\" ao evento: {$conferenceName} foi-lhe designada e permitirá que a consulte como se fosse um coordenador de painel por todo o processo editorial.\n\nURL da submissão: {$submissionUrl}\nNome do utilizador: {$directorUsername}\n\nObrigado,\n{$editorialContactSignature}','Este e-mail informa o coordenador de painel que o Presidente da Comissão Organizadora designou a tarefa de supervisionar uma submissão através do processo de edição. Contém informações sobre a submissão e como aceder ao portal da conferência'),
('REVIEW_REQUEST','pt_PT','Pedido de Revisão','{$reviewerName}:\n\nAcreditamos que seria um excelente avaliador para a proposta, \"{$paperTitle},\" a qual foi submetida para {$conferenceName}. O extrato da submissão está inserido abaixo, esperamos que considere o convite desta importante tarefa.\n\nPor favor, entre no site da conferência até {$weekLaterDate} para aceitar, ou não, o convite de revisão. Use o mesmo Site para aceder à submissão, registar a revisão e recomendações. O endereço do site é: {$conferenceUrl}\n\nA revisão tem de ser feita até {$reviewDueDate}.\n\nCaso não possua um utilizador (email) e senha de acesso ao site da conferência, pode-se utilizar este link para trocar a senha (que será enviada para o seu email) : {$passwordResetUrl} \n\nEndereço de submissão {$submissionReviewUrl}\n\nGrato por considerar o nosso pedido.\n\n{$editorialContactSignature}\n\n\n\n\"{$paperTitle}\"\n\nResumo\n{$paperAbstract}','Este é email do coordenador de painel para o avaliador para que aceite ou não a tarefa de avaliar uma submissão. Fornece informações sobre a submissão como: um pequeno resumo, a data final para a revisão e como aceder à submissão .'),
('REVIEW_REQUEST_ONECLICK','pt_PT','pedido de revisão','{$reviewerName}:\n\nAcreditamos que seria um excelente avaliador para a proposta, \"{$paperTitle},\" a qual foi submetida para {$conferenceName}. O extracto da submissão está inserido abaixo, esperamos que considere o convite desta importante tarefa.\n\nPor favor, entre no site da conferência até {$weekLaterDate} para aceitar, ou não, o convite de revisão. Use o mesmo Site para aceder à submissão, registar a revisão e recomendações. O endereço do site é: {$conferenceUrl}\n\nA revisão tem de ser feita até {$reviewDueDate}.\n\nEndereço de submissão {$submissionReviewUrl}\n\nGrato por considerar o nosso pedido.\n\n{$editorialContactSignature}\n\n\n\n\"{$paperTitle}\"\n\nResumo\n{$paperAbstract}','Este email do coordenador de painel para o avaliador para que aceite ou não a tarefa de avaliar uma submissão. Fornece informações sobre a submissão como: um pequeno resumo, a data final para a revisão e como aceder à submissão. Esta mensagem é utilizada quando o processo de revisão padrão é seleccionado na configuração da conferência, passo 2 e o acesso ao avaliador one-click está disponível.'),
('REVIEW_CANCEL','pt_PT','Pedido de revisão cancelado','{$reviewerName}:\n\nDecidimos cancelar o nosso pedido de revisão para a submissão:  \"{$paperTitle},\" para a  {$conferenceName}. Pedimos sinceras desculpas pelos inconvenientes que esta decisão possa ter causado. Esperamos poder contar com sua colaboração nesta ou em futuras conferências. \n\nCaso tenha alguma dúvida, por favor entre em contacto.\n\n{$editorialContactSignature}','Este email é enviado do coordenador de painel para o avaliador que possui uma revisão de uma submissão em progresso, para notificá-lo que a revisão pode ser cancelada.'),
('REVIEW_CONFIRM','pt_PT','Disponível para Avaliar','{$editorialContactName}:\n\nEstou disponível para avaliar a submissão: \"{$paperTitle},\" para a  {$conferenceName}. Obrigado pela consideração. Espero ter a revisão concluída até: {$reviewDueDate}, podendo ser antes.\n\n{$reviewerName}','Este email é enviado para o coordenador de painel em resposta ao pedido de revisão para avisá-lo que o pedido de revisão foi aceite e será concluído na data especificada.'),
('REVIEW_CONFIRM_ACK','pt_PT','Pedido de Revisão aceite','{$reviewerName}:\n\nGrato por aceitar avaliar a  \"{$paperTitle}\" para a {$conferenceName} até {$reviewDueDate}.\n\n{$editorialContactSignature}','Este email é enviado do coordenador de painel para o avaliador a informá-lo sobre a sua aceitação da tarefa de revisão ou encorajá-lo para concluir a revisão.'),
('REVIEW_DECLINE','pt_PT','indisponível para avaliar','{$editorialContactName}:\n\nTemo que, neste momento, esteja indisponível para avaliar a submissão, \"{$paperTitle},\" para a  {$conferenceName}. Agradeço o lembrete e espero poder ajudar em outra oportunidade.\n\n{$reviewerName}','Este email é enviado do Avaliador para o Director em resposta a um pedido de revisão no sentido de informar o Drector que o pedido de revisão foi cancelado.'),
('REVIEW_COMPLETE','pt_PT','revisão concluída','{$editorialContactName}:\n\nCompletei a revisão de  \"{$paperTitle}\" para {$conferenceName}, e submeti as recomendações: \"{$recommendation}.\"\n\n{$reviewerName}','Este email é enviado do Avaliador para o Director a informá-lo que a revisão foi conluída e que os comentários e recomendações foram registadas no site da conferência.'),
('REVIEW_ACK','pt_PT','Reconhecimento pela revisão','{$reviewerName}:\n\nObrigado por completar a revisão da submissão, \"{$paperTitle},\" para{$conferenceName}. Agradecemos o seu contributo para a qualidade dos trabalhos produzidos.\n\n{$editorialContactSignature}','Este email é enviado pelo coordenador de painel para confirmar o recebimento da revisão completa e agradecer ao avaliador pela sua contribuição.'),
('REVIEW_REMIND','pt_PT','Lembrete sobre a revisão','{$reviewerName}:\n\nEsta mensagem serve para lembrá-lo sobre o pedido de revisão da submissão, \"{$paperTitle},\" para {$conferenceName}. Esperamos que tenha avaliado até {$reviewDueDate}, e seria conveniente recebermos a sua resposta o quanto antes.\n\nCaso não tenha utilizador e senha para o site do evento, use este link para trocar a senha (será enviado uma mensagem para o utilizador - Email). {$passwordResetUrl}\n\nLink da submissão: {$submissionReviewUrl}\n\nPor favor confirme a sua disponibilidade para completar esta importante contribuição para o trabalho deste evento. Esperamos notícias suas.\n\n{$editorialContactSignature}','Este email é enviado pelo coordenador de painel para lembrar o avaliador sobre a data de entrega da revisão.'),
('REVIEW_REMIND_ONECLICK','pt_PT','Lembrete sobre a revisão','{$reviewerName}:\n\nEsta mensagem é apenas para lembrá-lo sobre o pedido de revisão da submissão, \"{$paperTitle},\" para {$conferenceName}. Esperamos que tenha avaliado até {$reviewDueDate}, e seria conveniente recebermos a sua resposta o quanto antes.\n\nlink da submissão: {$submissionReviewUrl}\n\nPor favor confirme a sua disponibilidade para completar esta importante contribuição para o trabalho deste evento. Esparamos notícias suas.\n\n{$editorialContactSignature}','Este email é enviado pelo coordenador de painel para lembrar o avaliador sobre a data limite.'),
('REVIEW_REMIND_AUTO','pt_PT','Lembrete automático sobre a revisão','{$reviewerName}:\n\nEsta mensagem é apenas para lembrá-lo sobre o pedido de revisão da submissão, \"{$paperTitle},\" para {$conferenceName}. Esperamos que tenha avaliado até {$reviewDueDate}. Este email foi gerado e enviado automaticamente pois o prazo de revisão foi atingido. Seria conveniente recebermos a sua resposta o quanto antes.\n\nCaso não tenha nome de utilizador e senha para o site do evento, use este link para trocar a senha (será enviado uma mensagem para o utilizador - Email). {$passwordResetUrl}\n\nlink da submissão: {$submissionReviewUrl}\n\nPor favor confirme a sua disponibilidade para completar esta importante contribuição para o trabalho deste evento. Esperamos notícias suas.\n\n{$editorialContactSignature}','Este email é enviado automaticamente, quando um avaliador ultrapassa uma data  (Veja: opções de revisão sob Configuração da Conferência, passo 2) e o acesso do avaliador ao one-click está indisponível. tarefas esquematizadas Scheduled devem estar disponíveis e configuradas (veja o arquivo de configuração do site).'),
('REVIEW_REMIND_AUTO_ONECLICK','pt_PT','Lembrete automático sobre a revisão','{$reviewerName}:\n\nEsta mensagem é apenas para lembrá-lo sobre o pedido de revisão da submissão, \"{$paperTitle},\" para {$conferenceName}. Esperamos que tenha avaliado até {$reviewDueDate}, este email foi gerado e enviado automaticamente pois o prazo de revisão foi atingido. Seria conveniente recebermos a sua resposta o quanto antes.\nSubmission URL: {$submissionReviewUrl}\n\nPor favor confirme a sua disponibilidade para completar esta importante contribuição para o trabalho deste evento. Esperamos notícias suas.\n\n{$editorialContactSignature}','Este email é enviado automaticamente, quando um avaliador ultrapassa uma data  (Veja: opções de revisão sob Configuração da Conferência, passo 2) e o acesso do avaliador ao one-click está indisponível. tarefas esquematizadas Scheduled  devem estar disponíveis e configuradas (veja o arquivo de configuração do site).'),
('SUBMISSION_ABSTRACT_ACCEPT','pt_PT','Decisão Editorial','{$presenterName}:\n\nParabéns, o resumo do seu documento {$paperTitle} foi aceite para ser apresentado na(o) {$conferenceTitle} que acontecerá {$conferenceDate} em {$locationCity}. \n\nObrigado e aguardamos a sua participação no evento.\n{$editorialContactSignature}','Este email é enviado pelo coordenador de painel ao autor da submissão para notificá-lo da sua decisão em relação a esta.'),
('SUBMISSION_ABSTRACT_DECLINE','pt_PT','Decisão Editorial','{$presenterName}:\n\nCompletamos a revisão do resumo do seu documento \"{$paperTitle}.\" O resultado foi ...\n\n{$editorialContactSignature}','Este email é enviado pelo Coordenador de Painel ao autor da submissão para notificá-lo da sua decisão em relação a esta.'),
('SUBMISSION_ABSTRACT_REVISE','pt_PT','Decisão Editorial','{$presenterName}:\n\nApós rigorosa revisão do resumo do seu documento, \"{$paperTitle}\" aceitar-se-á para ser apresentado na(o) {$conferenceTitle} desde que as seguintes modificações sejam realizadas.\n\nObrigado e aguardamos a sua participação no evento.\n\n{$editorialContactSignature}','Este email é enviado pelo coordenador de painel ao autor da submissão para notificá-lo da sua decisão em relação a esta.'),
('SUBMISSION_PAPER_ACCEPT','pt_PT','Decisão Editorial','{$presenterName}:\n\nParabéns, o seu documento {$paperTitle} foi aceite para ser apresentado na(o) {$conferenceTitle} que acontecerá {$conferenceDate} em {$locationCity}. \n\nObrigado e aguardamos a sua participação no evento.\n{$editorialContactSignature}','Este email é enviado pelo coordenador de painel ao autor da submissão para notificá-lo da sua decisão em relação a esta.'),
('SUBMISSION_PAPER_DECLINE','pt_PT','Decisão Editorial','{$presenterName}:\n\nCompletamos a revisão de seu documento \"{$paperTitle}.\" O resultado foi ...\n\n{$editorialContactSignature}','Este email é enviado pelo coordenador de painel ao autor da submissão para notificá-lo da sua decisão em relação a esta.'),
('SUBMISSION_PAPER_REVISE','pt_PT','Decisão Editorial','{$presenterName}:\n\nApós rigorosa revisão do seu documento, \"{$paperTitle}\" aceitar-se-á para ser apresentado na(o) {$conferenceTitle} desde que as seguintes modificações sejam realizadas.\n\nObrigado e aguardamos a sua participação no evento.\n\n{$editorialContactSignature}','Este email é enviado pelo coordenador de painel ao autor da submissão para notificá-lo da sua decisão em relação a esta.'),
('REGISTRATION_NOTIFY','pt_PT','Notificação de Inscrição','{$registrantName}:\n\nVocê encontra-se registado neste sistema para a(o) {$conferenceName}, com o seguinte registo:\n\n{$registrationType}\n\nPara aceder ao conteúdo disponível somente a participante registados, entre no sistema com o seu login, \"{$username}\".\n\nApós aceder ao sistema, poderá alterar o seu perfil e senha a qualquer momento.\n\nSe você possuir uma inscrição institucional, não será necessário aos utilizadores da instituição se conectarem, uma vez que as requisições para o conteúdo restrito serão automaticamente autenticadas pelo sistema.\n\nEm caso de dúvidas, ente em contacto.\n\n{$registrationContactSignature}','Este email notifica o leitor que o administrador criou um registo para ele. Informa também sobre a URL da conferência bem como instruções de acesso.'),
('OPEN_ACCESS_NOTIFY','pt_PT','Acesso Livre','Leitores:\n\n{$conferenceName} abriu o acesso a novos artigos. Convidamos a ler o sumário e visitar o nosso portal ({$conferenceUrl}) para aceder aos documentos e artigos do seu interesse.\n\nAgradecemos o interesse pelo nosso trabalho,\n{$editorialContactSignature}','Este email é enviado a leitores inscritos que tenham solicitado receber avisos quando novos artigos estão disponíveis para acesso livre.'),
('USER_VALIDATE','pt_PT','Validação de Registo','{$userFullName}\n\nO seu registo foi criado, sob o nome  {$conferenceName}.\nNo entanto, para poder usá-lo, é necessário validar o seu e-mail.\nPara validar o seu e-mail, basta clicar no link a seguir:\n\n{$activateUrl}\n\nAtenciosamente,\n{$principalContactSignature}','Este e-mail é enviado ao utilizador recentemente registado para validação de conta de e-mail.'),
('PASSWORD_RESET_CONFIRM','pt_BR','Confirmação de alteração de senha','Recebemos uma solicitação de alteração da sua senha para o portal {$siteTitle}.\n\nSe você não efetuou esta solicitação, por favor ignore este e-mail e sua senha não será alterada. Se deseja alterar sua senha, clique no endereço abaixo.\n\nAlterar minha senha: {$url}\n\n{$principalContactSignature}','Este e-mail é enviado aos usuários inscritos quando eles esqueceram sua senha e não conseguem efetuar o login. Ele envia um endereço para que eles possam alterar sua senha.'),
('PASSWORD_RESET','pt_BR','Senha alterada','Sua senha foi redefinida com sucesso para o portal {$siteTitle}. Por favor, guarde login e senha, pois são necessários em todas as interações com o sistema da conferência.\n\nSeu login: {$username}\nSua senha: {$password}\n\n{$principalContactSignature}','Este e-mail é enviado aos usuários inscritos quando eles redefinem sua senha, ao efetuarem, com sucesso, o processo descrito no e-mail PASSWORD_RESET_CONFIRM.'),
('USER_REGISTER','pt_BR','Inscrição de novo usuário','Obrigado por sua inscrição no evento: {$conferenceName}. Por favor, anote seus dados abaixo, pois serão necessários em todas as interações com o sistema da conferência.\n\nLogin: {$username}\nSenha: {$password}\n\nObrigado,\n{$principalContactSignature}','Este e-mail é enviado aos usuários recém inscritos dando boas vindas ao sistema e lhes informando seus dados de usuário e senha.'),
('SUBMISSION_ACK','pt_BR','Aviso de recebimento de submissão','{$authorName}:\n\nObrigado por sua submissão, \"{$paperTitle}\" ao evento: {$conferenceName}. Com o nosso sistema de gestão de conferências online, você poderá acompanhar o progresso de sua submissão através do processo editorial, ao acessar o portal da conferência:\n\nURL da submissão: {$submissionUrl}\nLogin: {$authorUsername}\n\nQuaisquer dúvidas, por favor entre em contato. \nObrigado por considerar esta conferência um local para publicar seu trabalho.\n\n{$editorialContactSignature}','Este e-mail, quando ativo, é enviado automaticamente para o autor quando ele(a) completa o processo de submeter um artigo ou resumo \"Abstract\" à conferência. Ele fornece informações sobre o acompanhamento do processo de submissão  e agradece ao autor pela submissão.'),
('SUBMISSION_UPLOAD_ACK','pt_BR','Aviso de recebimento de arquivo de submissão','{$authorName}:\n\nObrigado por enviar sua apresentação, \"{$paperTitle}\" ao evento: {$conferenceName}. Com o nosso sistema de gestão de conferências online, você poderá acompanhar o progresso de sua submissão através processo editorial, ao acessar o portal da conferência:\n\nURL da submissão: {$submissionUrl}\nLogin: {$authorUsername}\n\nQuaisquer dúvidas, por favor entre em contato. \nObrigado por considerar esta conferência um local para o seu trabalho.\n\n{$editorialContactSignature}','Este e-mail, quando ativo, é enviado automaticamente para o autor quando ele(a) completa o processo de submeter um artigo ou resumo \"Abstract\" à conferência. Ele fornece informações sobre o acompanhamento do processo de submissão  e agradece ao autor pela submissão.'),
('SUBMISSION_UNSUITABLE','pt_BR','Submissão não apropriada','{$authorName},\n\nUma revisão inicial de \"{$paperTitle}\" determinou que a submissão não se enquadra no escopo e foco do evento {$conferenceName}. Recomendo que consulte a descrição desta conferência no menu \"Sobre\", assim como outras conferência anteriores, para saber mais sobre os tipos de trabalho aceitos. Considere efetuar a submissão deste artigo ou resumo a outra conferência em consonância com o trabalho.\n\n{$editorialContactSignature}',''),
('SUBMISSION_COMMENT','pt_BR','Comentário da Submissão','{$name},\n\n{$commentName} adicionou um comentário à sua submissão, \"{$paperTitle}\" no evento {$conferenceName}:\n\n{$comments}','Este e-mail notifica várias pessoas envolvidas no processo de editoração de uma submissão que recebeu num novo comentário'),
('SUBMISSION_DECISION_REVIEWERS','pt_BR','Decisão sobre \"{$paperTitle}\"','Como um dos revisores da submissão \"{$paperTitle},\" encaminhada ao evento {$conferenceName}, estou enviando as avaliações e decisões editoriais enviadas ao autor, após a conclusão do processo de avaliação.\n\nObrigado novamente por sua importante participação neste processo.\n \n{$editorialContactSignature}\n\n{$comments}','Este e-mail informa aos revisores de uma submissão que o processo de revisão foi finalizado. Ele inclui informações sobre o artigo e a decisão alcançada, e agradece aos revisores por suas contribuições'),
('DIRECTOR_ASSIGN','pt_BR','Tarefa designada pelo Diretor','{$editorialContactName},\n\nA submissão \"{$paperTitle}\", encaminhada ao evento {$conferenceName}, lhe foi designada para que a acompanhe durante o processo editorial, de acordo com seu papel de Diretor de Modalidade.\n\nURL da submissão: {$submissionUrl}\nLogin: {$directorUsername}\n\nObrigado!\n\nAtenciosamente,\n{$editorialContactSignature}','Este e-mail informa a um Diretor de Modalidade que o Diretor designou a tarefa de supervisionar uma submissão através do processo de editoração. Ele contém informações sobre a submissão e como acessar o portal da conferência'),
('REVIEW_REQUEST','pt_BR','Solicitação de avaliação','{$reviewerName},\n\nAcreditamos que seria um excelente avaliador para a submissão \"{$paperTitle},\" submetida para a conferência {$conferenceName}. O extrato da submissão está inserido abaixo, e esperamos que considere aceitar o convite para assumir essa importante tarefa.\n\nPor favor, entre no site da conferência até {$weekLaterDate} para aceitar ou não o convite de avaliação. Use o mesmo site para acessar a submissão, registrar sua avaliação e recomendações. \nO endereço do site é: {$conferenceUrl}\n\nO prazo para concluir a avaliação e até {$reviewDueDate}.\n\nCaso não possua seu login e senha de acesso ao site da conferência, faça uso deste link para trocar a senha (que será enviada para o seu email) : {$passwordResetUrl} \n\nURL da submissão {$submissionReviewUrl}\n\nAgradecemos considerar nossa solicitação.\n\n{$editorialContactSignature}\n\n\n\n\"{$paperTitle}\"\n\nResumo\n{$paperAbstract}','Este email do diretor de modalidades para o avaliador para que aceite ou não a tarefa de avaliar uma submissão. Fornece informações sobre a submissão como: um pequeno resumo, a data final para a avaliação e como acessar a submissão .'),
('REVIEW_REQUEST_ONECLICK','pt_BR','Solicitação de avaliação','{$reviewerName}:\n\nAcreditamos que seria um excelente avaliador para a proposta \"{$paperTitle}\", submetida para a conferência {$conferenceName}. O extrato da submissão está inserido abaixo, e esperamos que considere aceitar o convite para assumir essa importante tarefa.\n\nPor favor, entre no site da conferência até {$weekLaterDate} para aceitar ou não o convite de avaliação. Use o mesmo site para acessar a submissão, registrar sua avaliação e recomendações. \nO endereço do site é: {$conferenceUrl}\n\nO prazo para concluir a avaliação é até {$reviewDueDate}.\n\nURL da submissão {$submissionReviewUrl}\n\nAgradecemos considerar nossa solicitação.\n\n{$editorialContactSignature}\n\n\n\n\"{$paperTitle}\"\n\nResumo\n{$paperAbstract}','Este email do diretor de modalidades para o avaliador para que aceite ou não a tarefa de avaliar uma submissão. Fornece informações sobre a submissão como: um pequeno resumo, a data final para a avaliação e como acessar a submissão. Esta mensagem é utilizada quando o  processo de avaliação padrão é selecionado na configuração da conferência, passo 2 e o acesso ao avaliador one-click está disponível.'),
('REVIEW_CANCEL','pt_BR','Solicitação de avaliação cancelada','{$reviewerName},\n\nInfelizmente decidimos cancelar nossa solicitação de avaliação para a submissão \"{$paperTitle},\" enviada à conferência {$conferenceName}. Pedimos sinceras desculpas pelo inconveniente que o cancelamento possa causar. \n\nEsperamos poder contar com sua ajuda nessa ou em outra conferência. \n\n?Em caso de dúvidas, entre em contato.\n\n{$editorialContactSignature}','Este email é enviado do diretor de modalidade para o avaliador que possui uma avaliação de uma submissão em progresso, para notificá-lo que a avaliação pode ser cancelada.'),
('REVIEW_CONFIRM','pt_BR','Disponível para avaliar','{$editorialContactName},\n\nEstou disponível e disposto a avaliar a submissão \"{$paperTitle}\", enviada à conferência {$conferenceName}. Agradeço lembrar-se de mim e confiar no meu conhecimento para assumir esta importante tarefa. Espero concluir a avaliação antes do prazo definido, {$reviewDueDate}.\n\n{$reviewerName}','Este email é enviado para o diretor de modalidade em resposta ao pedido de avaliação para avisá-lo que o pedido de avaliação foi aceito e será terminado na data especificada.'),
('REVIEW_CONFIRM_ACK','pt_BR','Solicitação de avaliação aceito','{$reviewerName},\n\nAgradecemos por aceitar assumir a avaliação da submissão \"{$paperTitle}\", encaminhada à conferência {$conferenceName}, até o prazo de {$reviewDueDate}.\n\n{$editorialContactSignature}','Este email é enviado do diretor de modalidades para o avaliador, afim do conhecimento de sua aceitação de avaliação ou encorajá-lo para completar a avaliação.'),
('REVIEW_DECLINE','pt_BR','Indisponível para avaliar','{$editorialContactName},\n\nTemo que, neste momento, esteja indisponível para avaliar a submissão \"{$paperTitle},\" enviada à conferência {$conferenceName}. Agradeço lembrar-se de mim e confiar no meu conhecimento para assumir esta importante tarefa. Espero estar disponível em outra oportunidade.\n\n{$reviewerName}','This email is sent by a Reviewer to the Track Director in response to a review request to notify the Track Director that the review request has been declined.'),
('REVIEW_COMPLETE','pt_BR','Avaliação concluída','{$editorialContactName},\n\nConcluí a avaliação de \"{$paperTitle}\", enviada à conferência {$conferenceName}, e encaminho minha recomendação: \"{$recommendation}.\"\n\n{$reviewerName}','This email is sent by a Reviewer to the Track Director to notify them that a review has been completed and the comments and recommendations have been recorded on the conference web site.'),
('REVIEW_ACK','pt_BR','Agradecimento pela conclusão da avaliação','{$reviewerName},\n\nAgradecemos por ter concluído a avaliação da submissão \"{$paperTitle}\", enviada à conferência {$conferenceName}. Apreciamos sua importante contribuição para garantir a qualidade dos trabalhos que publicamos.\n\n{$editorialContactSignature}','Este emial é enviado pelo diretor de modalidades para confirmar o recebimento da avaliação completa e agradecer ao avaliador por sua contribuição.'),
('REVIEW_REMIND','pt_BR','Lembrete de avaliação pendente','{$reviewerName},\n\nEsta mensagem é apenas para lembrá-lo da solicitação de avaliação de \"{$paperTitle},\" enviado à conferência {$conferenceName}. Esperamos que tenha concluído os trabalhos até {$reviewDueDate}, e seria importante recebermos sua resposta o quanto antes.\n\nCaso não tenha seu login e senha para acessar o sistema, use o link a seguir para alterar a senha (que será enviada por e-mail junto com seu login). {$passwordResetUrl}\n\nURL da submissão: {$submissionReviewUrl}\n\nConfirme sua disponibilidade para concluir sua importante contribuição para garantir a qualidade dos trabalhos do evento.\n\nNão hesite em entrar em contato, em caso de dúvidas ou problemas.\n\n{$editorialContactSignature}','Este email é enviado pelo diretor de moddalidades para lembrar o avaliador da data de entrega da avaliação.'),
('REVIEW_REMIND_ONECLICK','pt_BR','Lembrete de avaliação pendente','{$reviewerName},\n\nEsta mensagem é apenas para lembrá-lo da solicitação de avaliação de \"{$paperTitle},\" enviado à conferência {$conferenceName}. Esperamos que tenha concluído os trabalhos até {$reviewDueDate}, e seria importante recebermos sua resposta o quanto antes.\n\nURL da submissão: {$submissionReviewUrl}\n\nConfirme sua disponibilidade para concluir sua importante contribuição para garantir a qualidade dos trabalhos do evento.\n\nNão hesite em entrar em contato, em caso de dúvidas ou problemas.\n\n{$editorialContactSignature}','Este email é enviado pelo diretor de modalidades para lembrar o avaliador da data limite.'),
('REVIEW_REMIND_AUTO','pt_BR','Lembrete automático de avaliação pendente','{$reviewerName},\n\nEsta mensagem é apenas para lembrá-lo da solicitação de avaliação de \"{$paperTitle},\" enviado à conferência {$conferenceName}. Esperamos que tenha concluído os trabalhos até {$reviewDueDate}. Esta mensagem automática foi enviada pois o prazo expirou, e seria importante recebermos sua resposta o quanto antes.\n\nCaso não tenha seu login e senha para acessar o sistema, use o link a seguir para alterar a senha (que será enviada por e-mail junto com seu login). {$passwordResetUrl}\n\nURL da submissão: {$submissionReviewUrl}\n\nConfirme sua disponibilidade para concluir sua importante contribuição para garantir a qualidade dos trabalhos do evento.\n\nNão hesite em entrar em contato, em caso de dúvidas ou problemas.\n\n{$editorialContactSignature}','Este email é enviado automaticamente, quando um avaliador ultrapassa uma data  (Veja: opções de avaliação sob Configuração da conferência, passo 2) e o acesso do avaliador ao one-click está indisponível. tarefas esquematizadas Scheduled  devem estar disponíveis e configuradas (veja o arquivo de configuração do site).'),
('REVIEW_REMIND_AUTO_ONECLICK','pt_BR','Lembrete automático de avaliação pendente','{$reviewerName},\n\nEsta mensagem é apenas para lembrá-lo da solicitação de avaliação de \"{$paperTitle},\" enviado à conferência {$conferenceName}. Esperamos que tenha concluído os trabalhos até {$reviewDueDate}. Esta mensagem automática foi enviada pois o prazo expirou, e seria importante recebermos sua resposta o quanto antes.\n\nURL da submissão: {$submissionReviewUrl}\n\nConfirme sua disponibilidade para concluir sua importante contribuição para garantir a qualidade dos trabalhos do evento.\n\nNão hesite em entrar em contato, em caso de dúvidas ou problemas.\n\n{$editorialContactSignature}','Este email é enviado automaticamente, quando um avaliador ultrapassa uma data  (Veja: opções de avaliação sob Configuração da conferência, passo 2) e o acesso do avaliador ao one-click está indisponível. tarefas esquematizadas Scheduled  devem estar disponíveis e configuradas (veja o arquivo de configuração do site).'),
('SUBMISSION_ABSTRACT_ACCEPT','pt_BR','Decisão editorial','{$authorName},\n\nParabéns, o resumo de seu documento {$paperTitle} foi aceito para ser apresentado na conferência {$conferenceTitle} que acontecerá no dia {$conferenceDate}, em {$locationCity}. \n\nAgradecemos considerar esta conferência para publicar seus trabalhos e aguardamos sua presença no evento.\n\n{$editorialContactSignature}','Este email é enviado pelo Diretor de Modalidade ao autor da submissão para notifica-lo de sua decisão em relação a ela.'),
('SUBMISSION_ABSTRACT_DECLINE','pt_BR','Decisão editorial','{$authorName},\n\nConcluímos a avaliação do resumo da submissão\"{$paperTitle}.\" \n\nO resultado é ...\n\n{$editorialContactSignature}','Este email é enviado pelo Diretor de Modalidade ao autor da submissão para notifica-lo de sua decisão em relação a ela.'),
('SUBMISSION_ABSTRACT_REVISE','pt_BR','Decisão editorial','{$authorName},\n\nApós cuidadosa avaliação da sua submissão, o documento \"{$paperTitle}\" será considerado para publicação na conferência {$conferenceTitle} desde que as seguintes alterações sejam realizadas.\n\nAgradecemos a atenção dispensada e aguardamos sua participação no evento.\n\n{$editorialContactSignature}','Este email é enviado pelo Diretor de Modalidade ao autor da submissão para notifica-lo de sua decisão em relação a ela.'),
('SUBMISSION_PAPER_ACCEPT','pt_BR','Decisão Editorial','{$authorName},\n\nParabéns, o seu documento {$paperTitle} foi aceito para ser apresentado na conferência {$conferenceTitle} que acontecerá no dia {$conferenceDate}, em {$locationCity}. \n\nObrigado e aguardamos sua participação no evento.\n{$editorialContactSignature}','Este email é enviado pelo Diretor de Modalidade ao autor da submissão para notifica-lo de sua decisão em relação a ela.'),
('SUBMISSION_PAPER_DECLINE','pt_BR','Decisão Editorial','{$authorName},\n\nConcluímos a avaliação do documento \"{$paperTitle}.\" \n\nO resultado foi ...\n\n{$editorialContactSignature}','Este email é enviado pelo Diretor de Modalidade ao autor da submissão para notifica-lo de sua decisão em relação a ela.'),
('SUBMISSION_PAPER_REVISE','pt_BR','Decisão Editorial','{$authorName},\n\nApós cuidadosa avaliação de sua submissão, o documento \"{$paperTitle}\" sera aceito para ser apresentado na conferência {$conferenceTitle}, desde que as seguintes alterações sejam realizadas.\n\nAgradecemos a atenção dispensada e aguardamos sua participação no evento.\n\n{$editorialContactSignature}','Este email é enviado pelo Diretor de Modalidade ao autor da submissão para notifica-lo de sua decisão em relação a ela.'),
('REGISTRATION_NOTIFY','pt_BR','Notificação de inscrição','{$registrantName},\n\nVocê se inscreveu em nosso sistema para o evento {$conferenceName}, com os seguintes dados: \n\n{$registrationType}\n\nPara acessar o conteúdo disponível somente à usuários cadastrados, acesse o sistema com seu login, \"{$username}\".\n\nApós acessar o sistema, poderá alterar seu perfil e senha a qualquer tempo.\n\nCaso seja uma inscrição institucional, não será necessário aos usuários da instituição se logarem, uma vez que as requisições para o conteúdo restrito serão automaticamente autenticadas pelo sistema.\n\nEm caso de dúvidas, ente em contato.\n\n{$registrationContactSignature}','Este email notifica o leitor que o gerente criou um registro para ele. Informa também  a URL da conferencia bem como instruções de acesso.'),
('OPEN_ACCESS_NOTIFY','pt_BR','Conferência de Acesso Livre','Prezados Leitores,\n\nInformamos que a conferência {$conferenceName} publicou no formato de acesso livre uma nova edição. Convidamos a ler o sumário e visitar nosso portal ({$conferenceUrl}) para acesso aos documentos e itens de seu interesse.\n\nAgradecemos seu apoio e interesse constantes em nosso trabalho.\n\n{$editorialContactSignature}','Este email é enviado a leitores inscritos que tenham solicitado receber aviso quando novos itens estão disponíveis ao acesso.'),
('USER_VALIDATE','pt_BR','Validação de Cadastro','{$userFullName},\n\nSeu cadastro foi criado na conferência {$conferenceName}, mas antes que possa usufruir do mesmo, é necessário validá-lo.\nPara isto, basta clicar no link de ativação de cadastro a seguir:\n\n{$activateUrl}\n\nAtenciosamente,\n{$principalContactSignature}','Este e-mail é enviado ao usuário recentemente cadastrado para validação de conta de e-mail.'),
('NOTIFICATION','pt_BR','Nova notificação de {$siteTitle}','Você tem uma nova notificação de {$siteTitle}:\n\n{$notificationContents}\n\nLink: {$url}\n\n{$principalContactSignature}',''),
('NOTIFICATION_MAILLIST','pt_BR','Nova notificação de {$siteTitle}','Você recebeu uma nova notificação de {$siteTitle}:\n--\n{$notificationContents}\n\nLink: {$url}\n--\n\nCaso deseje não receber mais estas notificações, acesse {$unsubscribeLink} e informe seu e-mail e senha para cancelar a assinatura.\n\n{$principalContactSignature}',''),
('NOTIFICATION_MAILLIST_WELCOME','pt_BR','Bem-vindo a lista de mensagens de {$siteTitle}','Você se cadastrou para receber notificações de {$siteTitle}.\n\nClique no link a seguir para confirmar a solicitação e incluir seu e-mail na lista de mensagens: {$confirmLink}\n\nCaso não deseje receber mais as notificações, acesse {$unsubscribeLink} e informe seu e-mail e senha para cancelar o serviço.\n\nSua senha para desabilitar as notificações é: {$password}\n\n{$principalContactSignature}',''),
('NOTIFICATION_MAILLIST_PASSWORD','pt_BR','Informações de cadastro na lista de mensagens de {$siteTitle}','Sua nova senha para desabilitar o serviço é: {$password}\n\nCaso não deseje mais receber as notificações, acesse {$unsubscribeLink} e informe seu e-mail e senha para cancelar a assinatura.\n\n{$principalContactSignature}',''),
('MANUAL_PAYMENT_RECEIVED','pt_BR','Pagaemento recebido para a conferência {$conferenceName}','{$registrantName},\n\nRecebemos o pagamento para a conferência {$conferenceName} e sua inscrição foi atualizada.\n\nEm caso de dúvidas, entre em contato através deste e-mail.\n\n{$registrationContactSignature}',''),
('SUBMISSION_PAPER_INVITE','pt_BR','Decisão editorial','{$authorName},\n\nInformamos que o resumo do documento {$paperTitle} foi aceito para ser apresentado na conferência {$conferenceTitle}, que ocorrerá no dia {$conferenceDate}, em {$locationCity}. \n\nEnvie agora o texto completo para avaliação.\n\nAgradecemos seu interesse em publicar seu trabalho na nossa conferência e aguardamos sua presença no nosso evento.\n\n{$editorialContactSignature}',''),
('PAYPAL_INVESTIGATE_PAYMENT','en_US','Unusual PayPal Activity','Open Conference Systems has encountered unusual activity relating to PayPal payment support for the conference {$schedConfName}. This activity may need further investigation or manual intervention.\n			\nThis email was generated by Open Conference Systems\' PayPal plugin.\n\nFull post information for the request:\n{$postInfo}\n\nAdditional information (if supplied):\n{$additionalInfo}\n\nServer vars:\n{$serverVars}\n','This email template is used to notify a conference\'s primary contact that suspicious activity or activity requiring manual intervention was encountered by the PayPal plugin.'),
('PAYPAL_INVESTIGATE_PAYMENT','pt_BR','Atividade incomum no PayPal','O Open Conference Systems encontrou atividade incomum relacionada com o suporte ao pagamento via PayPal, da conferência {$schedConfName}. Esta atividade pode exigir investigação mais profunda ou intervenção manual.\n			\nEsta mensagem foi gerada automaticamente pelo plugin PayPal do Open Conference Systems.\n\nInformações completas da requisição:\n{$postInfo}\n\nInformações adicionais (caso fornecidas):\n{$additionalInfo}\n\nVariáveis do Servidor:\n{$serverVars}',''),
('MANUAL_PAYMENT_NOTIFICATION','en_US','Manual Payment Notification','A manual payment needs to be processed for the conference {$schedConfName} and the user {$userFullName} (username \"{$userName}\").\n\nThe item being paid for is \"{$itemName}\".\nThe cost is {$itemCost} ({$itemCurrencyCode}).\n\nThis email was generated by Open Conference Systems\' Manual Payment plugin.','This email template is used to notify a conference\'s registration contact that a manual payment was requested.'),
('MANUAL_PAYMENT_NOTIFICATION','pt_BR','Notificação de pagamento manual','É necessário realizar o processamento de um pagamento manual para participar da conferência {$schedConfName}, encaminhada pelo usuário {$userFullName} (login \"{$userName}\").\n\nO item adquirido é \"{$itemName}\".\nO valor é de {$itemCost} ({$itemCurrencyCode}).\n\nEsta mensagem foi gerada pelo Plugin de Pagamento Manual do OCS.','');
